var Juniper=window.Juniper||{};Juniper.global=Juniper.global||{};Juniper.global.setCookie=function(cookieName,cookieValue){if(typeof Juniper.global.cookieDomain==="undefined"){document.cookie=cookieName+"="+cookieValue+"; path=/"}else{document.cookie=cookieName+"="+cookieValue+"; path=/; domain="+Juniper.global.cookieDomain}};Juniper.global.getCookie=function(cookieName){var nameEQ=cookieName+"=";var chip=document.cookie.split(";");for(var i=0;i<chip.length;i++){while(chip[i].charAt(0)==" "){chip[i]=chip[i].substring(1,chip[i].length)}if(chip[i].indexOf(nameEQ)===0){return chip[i].substring(nameEQ.length,chip[i].length)}}return null};Juniper.global.delCookie=function(cookieName){var date=new Date();date.setTime(-1);document.cookie=cookieName+'= ""; expires='+date.toGMTString()+"path=/; domain="+Juniper.global.cookieDomain};